import{dataService}from"dataService";import{templateHandler}from"templateHandler";class NewsController{getTennisArticle(e){const t=e.params.id;return dataService.tennisArticle(t).then(e=>{console.log(e),templateHandler.setTemplate("article","#content",e)})}getSportArticle(e){const t=e.params.id;return dataService.sportArticle(t).then(e=>{console.log(e),templateHandler.setTemplate("article","#content",e)})}getTennisNews(){dataService.tennisNews().then(e=>{templateHandler.setTemplate("category","#content",e)})}getLatestSportNews(){dataService.latestSportNews().then(e=>{templateHandler.setTemplate("category","#content",e)})}getAll(){let e=[dataService.tennisNews(),dataService.latestSportNews(),dataService.recentPosts(),dataService.media()];Promise.all(e).then(e=>{let t=e[0],a=e[1],s=e[2],n=e[3];templateHandler.setTemplate("home","#content",{tennisNews:t,latestSportNews:a,recentPosts:s,media:n})})}postTennisComment(e){let t=e.params.name,a=e.params.email,s=e.params.message,n=`http://localhost:3000/api/news/tennisnews/${+window.location.href.split("tennisNews/")[1].split("/")[0]}`;const l={name:t,email:a,message:s};$.post(n,l),location.reload(!0)}postSportComment(e){let t=e.params.name,a=e.params.email,s=e.params.message,n=`http://localhost:3000/api/news/latestSportNews/${+window.location.href.split("latestSportNews/")[1].split("/")[0]}`;const l={name:t,email:a,message:s};$.post(n,l),location.reload(!0)}}const newsController=new NewsController;export{newsController};