class Modal{constructor(){this.openModalButton=$(".open-modal"),this.modal=$(".modal"),this.closeModalButton=$(".modal__close"),this.events()}events(){this.openModalButton.click(this.openModal.bind(this)),this.closeModalButton.click(this.closeModal.bind(this)),$(document).keyup(this.keyPressHandler.bind(this))}keyPressHandler(o){27===o.keyCode&&this.closeModal()}openModal(){return this.modal.addClass("modal--is-visible"),!1}closeModal(){this.modal.removeClass("modal--is-visible")}}const modal=new Modal;export{modal};